$(function ()
{
    var urls = window.location.pathname.toLowerCase().split("/");
    var url = "/" + urls[1];
    var href = "";
    var nc = 0;
    $('#menu a').each(function (i, n)
    {
        href = $(this).attr("href").toLowerCase();
        if (href != "/")
        {
            var hrefs = href.split("/");
            if (url == ("/" + hrefs[1]))
            {
                var p = $(this).parent().parents("li");
                //alert(p.html());
                if (p.size() > 0)
                    p.find("a:first").addClass("on");
                else
                    $(this).parent().find("a:first").addClass("on");
                nc = 1;
                return false;
            }
        }
    });

    if (nc == 0)
        $('#menu a:first').addClass("on");

    $("#menu > li:last").css("background", "none");
    $('#menu ul').addClass('children'); //所有子菜单
    $('#menu .children').prev('a').addClass('sublink'); //所有子菜单相邻的超级链接，即为子菜单中包含下级菜单的菜单添加一个样式来指示当前菜单还有子菜单

    var menuOver = function ()
    {
        /* 可以针对'出现'定义自己的动画效果 */
        //$("select").css("display","none");
        $(this).children('ul').show();
    };
    var menuOut = function ()
    {
        /* 可以针对'消失'定义自己的动画效果 */
        //$("select").css("display","");
        $(this).children('ul').hide();
    };
    $('#menu li').hover(menuOver, menuOut);
});